import { timestampToReadableDate } from "../time";

export function GetGraphSettings (type, data) {
  const commonOptions = {
    responsive: true,
    plugins: {
      legend: {
        position: "top",
      },
      title: {
        display: true,
        text: "Chart.js Bar Chart",
      },
    },
    tooltips: {
      callbacks: {
        label: (item) => `${item.yLabel} 째C`,
      },
    },
    scales: {
      y: {
        ticks: {
          callback: function (value, index, ticks) {
            return value + " 째C";
          },
        },
      },
    },
  };

  const label = data.map((data2) => {
    return timestampToReadableDate(data2.timestamp);
  });

  const chartData = {
    label,
    datasets: [
      {
        label: "Dataset 1",
        data: data.map((data2) => {
          if (type === "Temperature" || type === "TemperatureAVG") {
            return data2.temperature;
          } else if (type === "Pressure" || type === "PressureAVG") {
            return data2.pressure;
          } else if (type === "Level1") {
            return data2.level1_chemical;
          } else if (type === "Level2") {
            return data2.level2_chemical;
          } else if (type === "Level") {
            return data2.level2_chemical + data2.level1_chemical;

          }
        }),
        backgroundColor: "rgba(255, 99, 132, 0.5",
      },
    ],
  };

  return { options: commonOptions, label, chartData };
};
